INSERT INTO `blogs` (`id`, `name`, `image`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(3, 'Top 30 phối màu giày SB DUNK LOW mới 2023', '/storage/uploads/2023/03/04/jd1.JPG', 'Giày SB Dunk là một mẫu giày sneaker được thiết kế vào năm 1985. Ban đầu, giày SB Dunk được thiết kế để sử dụng như một đôi giày trượt ván dành cho các skaters, nhưng dần dà đôi giày rất được săn đón và sử dụng phổ biến, nay đã trở thành một dòng giày huyền thoại dành cho các tín đồ Sneaker', '<p>Gi&agrave;y SB Dunk l&agrave; một mẫu gi&agrave;y sneaker được thiết kế v&agrave;o năm 1985. Ban đầu, gi&agrave;y SB Dunk được thiết kế để sử dụng như một đ&ocirc;i gi&agrave;y trượt v&aacute;n d&agrave;nh cho c&aacute;c skaters, nhưng dần d&agrave; đ&ocirc;i gi&agrave;y rất được săn đ&oacute;n v&agrave; sử dụng phổ biến, nay đ&atilde; trở th&agrave;nh một d&ograve;ng gi&agrave;y huyền thoại d&agrave;nh cho c&aacute;c t&iacute;n đồ Sneaker.Gi&agrave;y SB Dunk c&oacute; độ hot v&agrave; sức h&uacute;t lớn đối với giới Sneaker, độ Hot của gi&agrave;y SB Dunk v&agrave;o năm 2002 - 2004 l&agrave; rất cao. Đến giờ SB Dunk vẫn duy tr&igrave; được độ hot v&agrave; l&ograve;ng trung th&agrave;nh của c&aacute;c người mang. L&yacute; do n&agrave;o m&agrave; một đ&ocirc;i gi&agrave;y Sneaker Hot đến vậy? Ch&iacute;nh l&agrave; việc SB Dunk lu&ocirc;n được tung ra rất l&agrave; nhiều m&agrave;u đẹp v&agrave; c&oacute; sự cộng hưởng Collab từ c&aacute;c thương hiệu nổi tiếng kh&aacute;c, cũng như việc được Pr nhiều bởi những t&iacute;n đồ sneaker tr&ecirc;n c&aacute;c trang mạng x&atilde; hội.</p>', 1, '2023-03-03 21:37:32', '2023-03-03 21:37:32'),
(4, 'Các chất liệu của đế giày Midsole', '/storage/uploads/2023/03/04/thethao1.JPG', 'Đôi giày thể thao mà chúng ta mang sử dụng hằng ngày, các bạn đã có từng thắc mắc đế giày làm từ chất liệu gì không ?. Tuỳ vào từng loại chất liệu chúng ta sẽ có một đôi giày sneaker phù hợp với từng mục đích mang của chúng ta', '<p>Đ&ocirc;i gi&agrave;y thể thao m&agrave; ch&uacute;ng ta mang sử dụng hằng ng&agrave;y, c&aacute;c bạn đ&atilde; c&oacute; từng thắc mắc đế gi&agrave;y l&agrave;m từ chất liệu g&igrave; kh&ocirc;ng ?. Tuỳ v&agrave;o từng loại chất liệu ch&uacute;ng ta sẽ c&oacute; một đ&ocirc;i gi&agrave;y sneaker ph&ugrave; hợp với từng mục đ&iacute;ch mang của ch&uacute;ng ta.</p>\r\n\r\n<p><span style=\"color:#000000\">Mỗi loại chất liệu đế Midsole sẽ c&oacute; những ti&ecirc;u ch&iacute; kh&aacute;c nhau như độ bền, khả năng hấp thụ lực sốc, khả năng phản hồi, độ ổn định, độ nhẹ.&nbsp;</span><br />\r\n<span style=\"font-size:16px\"><span style=\"font-family:Questrial\"><span style=\"background-color:#ffffff\"><span style=\"color:#252525\">C&aacute;c cấu tạo về đế giữa (MidSole) lu&ocirc;n c&oacute; sự đổi mới li&ecirc;n tục về chất liệu đế giữa từ PU, EVA, TPU cho đến PEBA. Sau đ&acirc;y l&agrave; những vật liệu đế MidSole phổ biến được sử dụng nhiều trong gi&agrave;y thể thao, nhất l&agrave; d&ograve;ng gi&agrave;y chạy bộ running.</span></span></span></span></p>\r\n\r\n<p><span style=\"color:#252525\">Đế EVA l&agrave; loại đế Foam phổ biến nhất hiện nay, được xuất hiện ở rất nhiều mẫu&nbsp;</span><a href=\"https://vsneakershop.weebly.com/\" style=\"color:#fd0000 !important; text-decoration:none; font-family:Questrial; font-size:16px; font-style:normal; font-variant-ligatures:normal; font-weight:300; text-align:justify; white-space:normal; background-color:#ffffff\"><span style=\"color:#000000\">gi&agrave;y sneaker</span></a><span style=\"color:#252525\">. N&oacute; cung cấp nhiều sự lựa chọn cho c&aacute;c nh&agrave; thiết kế để kết hợp n&oacute; với c&aacute;c vật liệu kh&aacute;c do t&iacute;nh linh hoạt m&agrave; đế EVA mang lại.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"color:#000000\">Độ Nặng : Nhẹ</span></li>\r\n	<li><span style=\"color:#000000\">Độ &Ecirc;m Cushioning : &Ecirc;m v&agrave; mượt.</span></li>\r\n	<li><span style=\"color:#000000\">Độ Nảy Đ&agrave;n Hồi : Kh&aacute;, ph&ugrave; hợp.</span></li>\r\n	<li><span style=\"color:#000000\">Độ Bền : Thấp.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"color:#000000\">Mẫu gi&agrave;y Lunarlon đ&igrave;nh đ&aacute;m một thời của Nike ch&iacute;nh l&agrave; sử dụng đế gi&agrave;y EVA</span></p>', 1, '2023-03-03 21:39:55', '2023-03-03 21:39:55'),
(5, 'Album giày Air Force 1 Low mới 2023 đang Hot', '/storage/uploads/2023/03/04/adidas.JPG', 'Tại sao giày Air Force 1 lại Hot đến vậy ? Giày Air Force 1 là một trong những dòng giày quen thuộc đối với cộng đồng giày Sneaker. Đôi giày này đã xuất hiện từ nhiều năm trước nhưng vẫn giữ được vị trí trong lòng nhiều người đến nay, khi mỗi năm đều tung ra rất nhiều phối màu colorway đẹp và chất', '<p>Tại sao gi&agrave;y Air Force 1 lại Hot đến vậy ? Gi&agrave;y Air Force 1 l&agrave; một trong những d&ograve;ng gi&agrave;y quen thuộc đối với cộng đồng gi&agrave;y Sneaker. Đ&ocirc;i gi&agrave;y n&agrave;y đ&atilde; xuất hiện từ nhiều năm trước nhưng vẫn giữ được vị tr&iacute; trong l&ograve;ng nhiều người đến nay, khi mỗi năm đều tung ra rất nhiều phối m&agrave;u colorway đẹp v&agrave; chất</p>\r\n\r\n<p>&nbsp;</p>', 1, '2023-03-03 21:41:37', '2023-03-03 21:41:37');

